// Extract OpenRouter model references from all workflows
const workflows = $input.all().map(i => i.json);
const modelsFound = [];

function getModelString(model) {
  if (typeof model === 'string') return model;
  if (model && typeof model === 'object') {
    return model.id || model.name || model.value || JSON.stringify(model);
  }
  return null;
}

for (const workflow of workflows) {
  if (!workflow.nodes) continue;

  for (const node of workflow.nodes) {
    // Check LangChain OpenRouter nodes
    if (node.type === '@n8n/n8n-nodes-langchain.lmChatOpenRouter') {
      const model = getModelString(node.parameters?.model);
      if (model) {
        modelsFound.push({
          model,
          workflowId: workflow.id,
          workflowName: workflow.name,
          nodeName: node.name,
          nodeType: 'LangChain OpenRouter'
        });
      }
    }

    // Check HTTP Request nodes calling OpenRouter API
    if (node.type === 'n8n-nodes-base.httpRequest') {
      const url = String(node.parameters?.url || '');
      if (url.includes('openrouter.ai')) {
        // Extract model from JSON body
        const jsonBody = String(node.parameters?.jsonBody || '');
        const modelMatch = jsonBody.match(/["']model["']\s*:\s*["']([^"']+)["']/);
        if (modelMatch) {
          modelsFound.push({
            model: modelMatch[1],
            workflowId: workflow.id,
            workflowName: workflow.name,
            nodeName: node.name,
            nodeType: 'HTTP Request'
          });
        }
      }
    }

    // Check OpenAI nodes with OpenRouter base URL in credentials
    if (node.type === '@n8n/n8n-nodes-langchain.lmChatOpenAi') {
      const model = getModelString(node.parameters?.model);
      // Flag these for manual review - might be using OpenRouter
      if (model && model.includes('/')) {
        modelsFound.push({
          model,
          workflowId: workflow.id,
          workflowName: workflow.name,
          nodeName: node.name,
          nodeType: 'OpenAI (possible OpenRouter)'
        });
      }
    }
  }
}

// Deduplicate models
const uniqueModels = [...new Set(modelsFound.map(m => m.model))];

return [{
  json: {
    totalWorkflows: workflows.length,
    modelsFound,
    uniqueModels
  }
}];

// Compare found models against OpenRouter available models
const modelsDevData = $input.first().json;
const extractedData = $('Extract OpenRouter Models').first().json;

// Get all OpenRouter models from models.dev
const openrouterProvider = modelsDevData.openrouter || {};
const availableModels = Object.keys(openrouterProvider.models || {});

// Also build a set of model IDs without provider prefix for fuzzy matching
const availableModelsSet = new Set(availableModels);
const availableModelsNormalized = new Set(
  availableModels.map(m => m.toLowerCase())
);

const results = [];
const deprecatedModels = [];

for (const found of extractedData.modelsFound) {
  const modelId = found.model;
  const modelLower = modelId.toLowerCase();

  // Check exact match
  let isAvailable = availableModelsSet.has(modelId);

  // Check normalized match
  if (!isAvailable) {
    isAvailable = availableModelsNormalized.has(modelLower);
  }

  // Check if model exists with different casing
  if (!isAvailable) {
    for (const available of availableModels) {
      if (available.toLowerCase() === modelLower) {
        isAvailable = true;
        break;
      }
    }
  }

  const result = {
    ...found,
    isAvailable,
    status: isAvailable ? 'OK' : 'NOT FOUND'
  };

  results.push(result);

  if (!isAvailable) {
    deprecatedModels.push(result);
  }
}

return [{
  json: {
    summary: {
      totalWorkflows: extractedData.totalWorkflows,
      totalModelsFound: extractedData.modelsFound.length,
      uniqueModels: extractedData.uniqueModels.length,
      deprecatedCount: deprecatedModels.length,
      availableOnOpenRouter: availableModels.length
    },
    deprecatedModels,
    allResults: results
  }
}];

// Format error message for Stop and Error node
const data = $input.first().json;
const deprecated = data.deprecatedModels;

let message = `Found ${deprecated.length} model(s) no longer available on OpenRouter:\n\n`;

for (const model of deprecated) {
  message += `• ${model.model}\n`;
  message += `  Workflow: ${model.workflowName} (${model.workflowId})\n`;
  message += `  Node: ${model.nodeName} (${model.nodeType})\n\n`;
}

message += `Scanned ${data.summary.totalWorkflows} workflows. OpenRouter has ${data.summary.availableOnOpenRouter} models.`;

return [{ json: { message } }];

// No issues found
const data = $input.first().json;

return [{
  json: {
    message: `✅ All ${data.summary.uniqueModels} OpenRouter models in use are still available.\nScanned ${data.summary.totalWorkflows} workflows.`,
    status: 'OK'
  }
}];
